import React from "react";
import { Link, useParams } from "react-router-dom";
import "../css/CoursePreview.css";

const mockCourse = {
  id: 1,
  title: "Intro to Python",
  instructor: "Jane Doe",
  duration: "4 weeks",
  topics: [
    "Variables and Data Types",
    "Control Structures",
    "Functions",
    "Modules and Packages"
  ],
  prerequisites: "No prior programming knowledge required.",
  rating: 4.6,
  reviews: 120,
};

const CoursePreview = () => {
  const { courseId } = useParams(); // To support dynamic routing

  // In real setup: fetch course based on courseId

  return (
    <div className="course-preview">
      <h2>{mockCourse.title}</h2>
      <p className="instructor">By <strong>{mockCourse.instructor}</strong></p>

      <p><strong>Duration:</strong> {mockCourse.duration}</p>
      <p><strong>Prerequisites:</strong> {mockCourse.prerequisites}</p>

      <div className="topics">
        <h3>Topics Covered</h3>
        <ul>
          {mockCourse.topics.map((topic, index) => (
            <li key={index}>{topic}</li>
          ))}
        </ul>
      </div>

      <div className="rating">
        ‚≠ê {mockCourse.rating} / 5 ({mockCourse.reviews} reviews)
      </div>

      <div className="cta">
        <p>To view lessons and quizzes, please log in or register.</p>
        <Link to="/login" className="btn">Log In</Link>
        <Link to="/register" className="btn secondary">Register</Link>
      </div>
    </div>
  );
};

export default CoursePreview;
