// StructureBuilder.jsx
import React, { useState, useRef } from "react";
import { ChevronDown, ChevronRight, PlusCircle, Edit2, Trash2, GripVertical } from "lucide-react";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import "../../../css/teacher/create/StructureBuilder.css";

const StructureBuilder = () => {
  const [modules, setModules] = useState([
    {
      id: "module-1",
      title: "Introduction to the Course",
      description: "Overview of what to expect in this course",
      isOpen: true,
      lessons: [
        { id: "lesson-1-1", title: "Welcome & Course Overview", duration: "10 min" },
        { id: "lesson-1-2", title: "How to Use This Platform", duration: "5 min" },
      ]
    },
    {
      id: "module-2",
      title: "Getting Started",
      description: "Fundamental concepts and tools setup",
      isOpen: false,
      lessons: [
        { id: "lesson-2-1", title: "Installation Guide", duration: "15 min" },
        { id: "lesson-2-2", title: "First Steps", duration: "20 min" },
      ]
    }
  ]);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [currentLesson, setCurrentLesson] = useState(null);
  const [currentModuleId, setCurrentModuleId] = useState(null);
  const nextModuleId = useRef(modules.length + 1);
  const nextLessonIds = useRef({});
  
  // Initialize nextLessonIds for existing modules
  modules.forEach(module => {
    if (!nextLessonIds.current[module.id]) {
      nextLessonIds.current[module.id] = module.lessons.length + 1;
    }
  });

  const toggleModule = (moduleId) => {
    setModules(modules.map(module => 
      module.id === moduleId 
        ? { ...module, isOpen: !module.isOpen } 
        : module
    ));
  };

  const addNewModule = () => {
    const newModuleId = `module-${nextModuleId.current}`;
    nextModuleId.current += 1;
    nextLessonIds.current[newModuleId] = 1;
    
    const newModule = {
      id: newModuleId,
      title: "New Module",
      description: "Module description",
      isOpen: true,
      lessons: []
    };
    
    setModules([...modules, newModule]);
  };

  const deleteModule = (moduleId) => {
    setModules(modules.filter(module => module.id !== moduleId));
  };

  const editModule = (moduleId, field, value) => {
    setModules(modules.map(module => 
      module.id === moduleId 
        ? { ...module, [field]: value } 
        : module
    ));
  };

  const addLesson = (moduleId) => {
    setCurrentLesson({
      id: "",
      title: "",
      duration: ""
    });
    setCurrentModuleId(moduleId);
    setIsModalOpen(true);
  };

  const editLesson = (lesson, moduleId) => {
    setCurrentLesson({ ...lesson });
    setCurrentModuleId(moduleId);
    setIsModalOpen(true);
  };

  const deleteLesson = (lessonId, moduleId) => {
    setModules(modules.map(module => 
      module.id === moduleId 
        ? { 
            ...module, 
            lessons: module.lessons.filter(lesson => lesson.id !== lessonId) 
          } 
        : module
    ));
  };

  const saveLesson = (lesson) => {
    if (!currentLesson.id) {
      // Add new lesson
      const newLessonId = `lesson-${currentModuleId.split('-')[1]}-${nextLessonIds.current[currentModuleId]}`;
      nextLessonIds.current[currentModuleId] += 1;
      
      setModules(modules.map(module => 
        module.id === currentModuleId 
          ? { 
              ...module, 
              lessons: [...module.lessons, { ...lesson, id: newLessonId }] 
            } 
          : module
      ));
    } else {
      // Update existing lesson
      setModules(modules.map(module => 
        module.id === currentModuleId 
          ? { 
              ...module, 
              lessons: module.lessons.map(l => 
                l.id === lesson.id ? lesson : l
              ) 
            } 
          : module
      ));
    }
    setIsModalOpen(false);
  };

  const handleDragEnd = (result) => {
    const { destination, source, type } = result;

    // If dropped outside a droppable area or at the same spot
    if (!destination || 
        (destination.droppableId === source.droppableId && 
         destination.index === source.index)) {
      return;
    }

    // Handle module reordering
    if (type === 'MODULE') {
      const reorderedModules = Array.from(modules);
      const [removed] = reorderedModules.splice(source.index, 1);
      reorderedModules.splice(destination.index, 0, removed);
      setModules(reorderedModules);
      return;
    }

    // Handle lesson reordering
    if (type === 'LESSON') {
      const sourceModuleId = source.droppableId;
      const destModuleId = destination.droppableId;
      
      // Create a new modules array to avoid direct state mutation
      const newModules = [...modules];
      
      // Find the source and destination modules
      const sourceModule = newModules.find(m => m.id === sourceModuleId);
      const destModule = newModules.find(m => m.id === destModuleId);
      
      // Copy the lessons arrays
      const sourceLessons = [...sourceModule.lessons];
      const destLessons = sourceModuleId === destModuleId ? 
        sourceLessons : [...destModule.lessons];
      
      // Remove the lesson from source and add to destination
      const [movedLesson] = sourceLessons.splice(source.index, 1);
      
      if (sourceModuleId === destModuleId) {
        // If within the same module
        sourceLessons.splice(destination.index, 0, movedLesson);
        sourceModule.lessons = sourceLessons;
      } else {
        // If between different modules
        destLessons.splice(destination.index, 0, movedLesson);
        sourceModule.lessons = sourceLessons;
        destModule.lessons = destLessons;
      }
      
      setModules(newModules);
    }
  };

  const LessonModal = () => {
    const [lessonData, setLessonData] = useState(currentLesson || { title: "", duration: "" });

    const handleChange = (e) => {
      const { name, value } = e.target;
      setLessonData({ ...lessonData, [name]: value });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      saveLesson(lessonData);
    };

    return (
      <div className="modal-overlay">
        <div className="modal-container">
          <h2 className="modal-title">
            {currentLesson?.id ? "Edit Lesson" : "Add New Lesson"}
          </h2>
          
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Lesson Title</label>
              <input
                type="text"
                name="title"
                value={lessonData.title}
                onChange={handleChange}
                className="form-input"
                placeholder="Enter lesson title"
                required
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Duration</label>
              <input
                type="text"
                name="duration"
                value={lessonData.duration}
                onChange={handleChange}
                className="form-input"
                placeholder="e.g. 15 min"
              />
            </div>
            
            <div className="modal-actions">
              <button
                type="button"
                onClick={() => setIsModalOpen(false)}
                className="btn-secondary"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="btn-primary"
              >
                Save
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  };

  return (
    <div className="structure-builder">
      <div className="structure-header">
        <h2 className="structure-title">Course Structure</h2>
        <p className="structure-subtitle">Build your course by adding modules and lessons</p>
      </div>
      
      <DragDropContext onDragEnd={handleDragEnd}>
        <Droppable droppableId="modules" type="MODULE">
          {(provided) => (
            <div 
              className="structure-content" 
              {...provided.droppableProps}
              ref={provided.innerRef}
            >
              {modules.length === 0 ? (
                <div className="empty-state">
                  No modules yet. Add your first module to get started.
                </div>
              ) : (
                modules.map((module, moduleIndex) => (
                  <Draggable
                    key={module.id}
                    draggableId={module.id}
                    index={moduleIndex}
                  >
                    {(provided) => (
                      <div 
                        className="module-item"
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                      >
                        <div className="module-header">
                          <div 
                            className="drag-handle"
                            {...provided.dragHandleProps}
                          >
                            <GripVertical size={18} />
                          </div>
                          
                          <button 
                            onClick={() => toggleModule(module.id)}
                            className="toggle-button"
                          >
                            {module.isOpen ? (
                              <ChevronDown size={18} />
                            ) : (
                              <ChevronRight size={18} />
                            )}
                          </button>
                          
                          <div className="module-info">
                            <input
                              type="text"
                              value={module.title}
                              onChange={(e) => editModule(module.id, 'title', e.target.value)}
                              className="module-title-input"
                            />
                            <input
                              type="text"
                              value={module.description}
                              onChange={(e) => editModule(module.id, 'description', e.target.value)}
                              className="module-description-input"
                            />
                          </div>
                          
                          <div className="module-actions">
                            <button 
                              onClick={() => deleteModule(module.id)}
                              className="delete-button"
                              aria-label="Delete module"
                            >
                              <Trash2 size={16} />
                            </button>
                          </div>
                        </div>
                        
                        {module.isOpen && (
                          <div className="lessons-container">
                            <Droppable
                              droppableId={module.id}
                              type="LESSON"
                            >
                              {(provided) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.droppableProps}
                                  className="lessons-list"
                                >
                                  {module.lessons.map((lesson, lessonIndex) => (
                                    <Draggable
                                      key={lesson.id}
                                      draggableId={lesson.id}
                                      index={lessonIndex}
                                    >
                                      {(provided) => (
                                        <div
                                          ref={provided.innerRef}
                                          {...provided.draggableProps}
                                          className="lesson-item"
                                        >
                                          <div 
                                            className="lesson-drag-handle"
                                            {...provided.dragHandleProps}
                                          >
                                            <GripVertical size={16} />
                                          </div>
                                          <div className="lesson-info">
                                            <div className="lesson-title">{lesson.title}</div>
                                            <div className="lesson-duration">{lesson.duration}</div>
                                          </div>
                                          <div className="lesson-actions">
                                            <button
                                              onClick={() => editLesson(lesson, module.id)}
                                              className="edit-button"
                                              aria-label="Edit lesson"
                                            >
                                              <Edit2 size={14} />
                                            </button>
                                            <button
                                              onClick={() => deleteLesson(lesson.id, module.id)}
                                              className="delete-button"
                                              aria-label="Delete lesson"
                                            >
                                              <Trash2 size={14} />
                                            </button>
                                          </div>
                                        </div>
                                      )}
                                    </Draggable>
                                  ))}
                                  {provided.placeholder}
                                  
                                  <button
                                    onClick={() => addLesson(module.id)}
                                    className="add-lesson-button"
                                  >
                                    <PlusCircle size={16} className="add-icon" />
                                    Add Lesson
                                  </button>
                                </div>
                              )}
                            </Droppable>
                          </div>
                        )}
                      </div>
                    )}
                  </Draggable>
                ))
              )}
              {provided.placeholder}
              
              <button
                onClick={addNewModule}
                className="add-module-button"
              >
                <PlusCircle className="add-icon" size={18} />
                Add New Module
              </button>
            </div>
          )}
        </Droppable>
      </DragDropContext>
      
      {isModalOpen && <LessonModal />}
    </div>
  );
};

export default StructureBuilder;
