import { useState } from 'react';
import {
  Home, BookOpen, Users, BarChart2,
  FileText, MessageCircle, Star, Video,
  Settings, ChevronRight, ChevronLeft, Bell,
  HelpCircle, LogOut, Calendar, User,
  Layers, MessageCircle as MessageCircleIcon, Award,
  ChevronDown
} from 'lucide-react';

export default function TeacherLayout({ children }) {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [notifications, setNotifications] = useState(3);
  const [activeItem, setActiveItem] = useState('dashboard');
  const [activeSubItem, setActiveSubItem] = useState('assignments');
  const [showProfileMenu, setShowProfileMenu] = useState(false);

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  const menuItems = [
    { id: 'dashboard', label: 'Dashboard', icon: Home, description: 'Overview of your courses, students and activity' },
    { id: 'courses', label: 'Course Management', icon: Layers, description: 'Create, edit and manage your course content' },
    { id: 'students', label: 'Student Management', icon: Users, description: 'View enrollments, progress and student data' },
    { id: 'discussions', label: 'Discussions & Forums', icon: MessageCircle, description: 'Manage course discussions and student questions' },
    { id: 'analytics', label: 'Analytics & Insights', icon: BarChart2, description: 'View detailed performance metrics and analytics' },
    { id: 'reviews', label: 'Reviews & Feedback', icon: Star, description: 'See student reviews and course ratings' },
    { id: 'sessions', label: 'Live Sessions', icon: Video, description: 'Schedule and manage webinars and office hours' },
    { id: 'calendar', label: 'Course Calendar', icon: Calendar, description: 'View and manage course schedule and deadlines' },
  ];

  const subMenuItems = [
    { id: 'overview', label: 'Overview' },
    { id: 'modules', label: 'Modules' },
    { id: 'assignments', label: 'Assignments & Grading' },
    { id: 'students', label: 'Students' },
    { id: 'settings', label: 'Settings' }
  ];

  return (
    <div className="sidebar-layout-container">
      {/* Sidebar */}
      <div className={`sidebar-sidebar ${sidebarOpen ? 'sidebar-sidebar-open' : 'sidebar-sidebar-collapsed'}`}>
        <div className="sidebar-sidebar-header">
          {sidebarOpen && <div className="sidebar-logo">Acadify</div>}
          <button onClick={toggleSidebar} className="sidebar-toggle-button">
            {sidebarOpen ? <ChevronLeft size={20} /> : <ChevronRight size={20} />}
          </button>
        </div>

        <div className="sidebar-profile-section">
          <div className="sidebar-avatar-container">
            <img src="/api/placeholder/48/48" alt="Teacher Profile" className={sidebarOpen ? "sidebar-avatar" : "sidebar-avatar-small"} />
            <span className="sidebar-status-indicator"></span>
          </div>
          {sidebarOpen && (
            <div className="sidebar-profile-details">
              <div className="sidebar-name">Prof. Jane Smith</div>
              <div className="sidebar-department">Computer Science</div>
              <div className="sidebar-badges">
                <span className="sidebar-badge sidebar-badge-instructor">Instructor</span>
                <span className="sidebar-badge sidebar-badge-rating">4.9 ★</span>
              </div>
            </div>
          )}
        </div>

        <nav className="sidebar-menu">
          <ul className="sidebar-menu-list">
            {menuItems.map(item => (
              <li key={item.id}>
                <button
                  onClick={() => setActiveItem(item.id)}
                  className={`sidebar-menu-item ${activeItem === item.id ? 'sidebar-active' : ''}`}
                  title={!sidebarOpen ? item.description : ""}
                >
                  <item.icon size={20} />
                  {sidebarOpen && <span className="sidebar-menu-label">{item.label}</span>}
                </button>
              </li>
            ))}
          </ul>
        </nav>

        <div className="sidebar-logout-section">
          <button className="sidebar-logout-button">
            <LogOut size={20} />
            {sidebarOpen && <span className="sidebar-menu-label">Logout</span>}
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="sidebar-main-content">
        <header className="sidebar-header">
          <div className="sidebar-header-top">
            <h1 className="sidebar-page-title">
              {menuItems.find(item => item.id === activeItem)?.label || 'Dashboard'}
            </h1>

            <div className="sidebar-header-actions">
              <div className="sidebar-search-bar">
                <input type="text" placeholder="Search courses, students, or content..." />
                <button className="sidebar-search-icon">
                  <svg className="sidebar-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                  </svg>
                </button>
              </div>
              <button className="sidebar-icon-button" title="Create New Course">
                <BookOpen size={20} />
              </button>
              <button className="sidebar-icon-button" title="Help Center">
                <HelpCircle size={20} />
              </button>
              <div className="sidebar-notification">
                <button className="sidebar-icon-button">
                  <Bell size={20} />
                  {notifications > 0 && <span className="sidebar-notification-badge">{notifications}</span>}
                </button>
              </div>
              <div className="sidebar-profile-dropdown">
                <button 
                  className="sidebar-icon-button" 
                  onClick={() => setShowProfileMenu(!showProfileMenu)}
                >
                  <img src="/api/placeholder/36/36" alt="Teacher" className="sidebar-avatar-small" />
                  <ChevronDown size={16} />
                </button>
                {showProfileMenu && (
                  <div className="sidebar-dropdown-menu">
                    <a href="#">Profile</a>
                    <a href="#">Account Settings</a>
                    <a href="#">Help Center</a>
                    <a href="#">Log Out</a>
                  </div>
                )}
              </div>
            </div>
          </div>

          {activeItem === 'courses' && (
            <div className="sidebar-submenu">
              {subMenuItems.map(item => (
                <button 
                  key={item.id}
                  className={activeSubItem === item.id ? 'sidebar-active-submenu' : ''}
                  onClick={() => setActiveSubItem(item.id)}
                >
                  {item.label}
                </button>
              ))}
            </div>
          )}

          <div className="sidebar-breadcrumbs">
            <span>Home</span>
            <span>/</span>
            <span>{menuItems.find(item => item.id === activeItem)?.label || 'Dashboard'}</span>
            {activeItem === 'courses' && (
              <>
                <span>/</span>
                <span>{subMenuItems.find(item => item.id === activeSubItem)?.label || 'Assignments & Grading'}</span>
              </>
            )}
          </div>
        </header>

        {/* Page Content */}
        <div className="sidebar-content">
          {children || (
            <div className="sidebar-content-card">
              <h2 className="sidebar-sidebar-content-title">
                Welcome to your {menuItems.find(item => item.id === activeItem)?.label || 'Dashboard'}
              </h2>
              <p className="sidebar-sidebar-content-description">
                {menuItems.find(item => item.id === activeItem)?.description || 'View and manage your teaching activities'}
              </p>
              
              {activeItem === 'courses' && activeSubItem === 'assignments' && (
                <div className="mt-6 p-4 bg-gray-50 border border-gray-200 rounded-md">
                  <h3 className="font-medium text-gray-800 mb-2">Assignments & Grading</h3>
                  <p className="text-gray-600 text-sm">
                    Create, manage and grade assignments, quizzes and exams for your courses. You can access this section from within any course.
                  </p>
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
