import React, { useState } from 'react';
import { LayoutGrid, PlusCircle, Edit2, Trash2, MoveVertical, ChevronDown, ChevronUp } from 'lucide-react';
import "../../../css/teacher/create/StructureBuilder.css";

const StructureBuilder = () => {
  const [sections, setSections] = useState([
    { id: 1, title: 'Introduction', expanded: true, subsections: [
      { id: 101, title: 'Course Overview' },
      { id: 102, title: 'Learning Objectives' }
    ]},
    { id: 2, title: 'Module 1: Fundamentals', expanded: false, subsections: [
      { id: 201, title: 'Lesson 1: Basic Concepts' },
      { id: 202, title: 'Lesson 2: Core Principles' }
    ]},
    { id: 3, title: 'Module 2: Advanced Topics', expanded: false, subsections: [
      { id: 301, title: 'Lesson 1: Advanced Methods' },
      { id: 302, title: 'Lesson 2: Case Studies' }
    ]}
  ]);

  const [editingSection, setEditingSection] = useState(null);
  const [editingSubsection, setEditingSubsection] = useState(null);
  const [newTitle, setNewTitle] = useState('');

  // Toggle section expansion
  const toggleSection = (id) => {
    setSections(sections.map(section => 
      section.id === id ? { ...section, expanded: !section.expanded } : section
    ));
  };

  // Add new section
  const addSection = () => {
    const newId = Math.max(...sections.map(s => s.id), 0) + 1;
    setSections([...sections, {
      id: newId,
      title: `New Section`,
      expanded: true,
      subsections: []
    }]);
  };

  // Add new subsection
  const addSubsection = (sectionId) => {
    setSections(sections.map(section => {
      if (section.id === sectionId) {
        const newSubId = section.subsections.length ? 
          Math.max(...section.subsections.map(s => s.id), 0) + 1 : 
          sectionId * 100 + 1;
        
        return {
          ...section,
          subsections: [...section.subsections, {
            id: newSubId,
            title: `New Subsection`
          }]
        };
      }
      return section;
    }));
  };

  // Start editing section
  const startEditSection = (section) => {
    setEditingSection(section.id);
    setNewTitle(section.title);
  };

  // Start editing subsection
  const startEditSubsection = (subsection) => {
    setEditingSubsection(subsection.id);
    setNewTitle(subsection.title);
  };

  // Save section edit
  const saveEditSection = () => {
    setSections(sections.map(section => 
      section.id === editingSection ? { ...section, title: newTitle } : section
    ));
    setEditingSection(null);
    setNewTitle('');
  };

  // Save subsection edit
  const saveEditSubsection = (sectionId) => {
    setSections(sections.map(section => {
      if (section.id === sectionId) {
        return {
          ...section,
          subsections: section.subsections.map(subsection => 
            subsection.id === editingSubsection ? { ...subsection, title: newTitle } : subsection
          )
        };
      }
      return section;
    }));
    setEditingSubsection(null);
    setNewTitle('');
  };

  // Delete section
  const deleteSection = (id) => {
    setSections(sections.filter(section => section.id !== id));
  };

  // Delete subsection
  const deleteSubsection = (sectionId, subsectionId) => {
    setSections(sections.map(section => {
      if (section.id === sectionId) {
        return {
          ...section,
          subsections: section.subsections.filter(subsection => subsection.id !== subsectionId)
        };
      }
      return section;
    }));
  };

  // Move section up
  const moveSectionUp = (index) => {
    if (index === 0) return;
    const newSections = [...sections];
    [newSections[index], newSections[index - 1]] = [newSections[index - 1], newSections[index]];
    setSections(newSections);
  };

  // Move section down
  const moveSectionDown = (index) => {
    if (index === sections.length - 1) return;
    const newSections = [...sections];
    [newSections[index], newSections[index + 1]] = [newSections[index + 1], newSections[index]];
    setSections(newSections);
  };

  // Move subsection up
  const moveSubsectionUp = (sectionId, index) => {
    if (index === 0) return;
    
    setSections(sections.map(section => {
      if (section.id === sectionId) {
        const newSubsections = [...section.subsections];
        [newSubsections[index], newSubsections[index - 1]] = [newSubsections[index - 1], newSubsections[index]];
        return { ...section, subsections: newSubsections };
      }
      return section;
    }));
  };

  // Move subsection down
  const moveSubsectionDown = (sectionId, index, subsections) => {
    if (index === subsections.length - 1) return;
    
    setSections(sections.map(section => {
      if (section.id === sectionId) {
        const newSubsections = [...section.subsections];
        [newSubsections[index], newSubsections[index + 1]] = [newSubsections[index + 1], newSubsections[index]];
        return { ...section, subsections: newSubsections };
      }
      return section;
    }));
  };

  return (
    <div className="structure-builder bg-white rounded-lg shadow-md p-6">
      <div className="builder-header flex items-center justify-between mb-6">
        <div className="flex items-center space-x-2">
          <LayoutGrid size={20} className="text-blue-600" />
          <h2 className="text-xl font-bold text-gray-800">Course Structure Builder</h2>
        </div>
        <button 
          onClick={addSection}
          className="flex items-center space-x-1 bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded-md transition-colors"
        >
          <PlusCircle size={16} />
          <span>Add Section</span>
        </button>
      </div>

      <div className="sections-container space-y-4">
        {sections.map((section, sectionIndex) => (
          <div key={section.id} className="section-item border border-gray-200 rounded-lg overflow-hidden">
            <div className="section-header bg-gray-50 p-4 flex items-center justify-between">
              {editingSection === section.id ? (
                <div className="flex items-center flex-grow mr-4">
                  <input 
                    type="text" 
                    value={newTitle} 
                    onChange={(e) => setNewTitle(e.target.value)}
                    className="flex-grow px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    onKeyPress={(e) => e.key === 'Enter' && saveEditSection()}
                    autoFocus 
                  />
                  <button 
                    onClick={saveEditSection}
                    className="ml-2 bg-green-500 hover:bg-green-600 text-white px-3 py-2 rounded-md"
                  >
                    Save
                  </button>
                </div>
              ) : (
                <div className="flex items-center flex-grow">
                  <button 
                    onClick={() => toggleSection(section.id)}
                    className="mr-2 text-gray-500 hover:text-gray-700"
                    aria-label={section.expanded ? "Collapse section" : "Expand section"}
                  >
                    {section.expanded ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
                  </button>
                  <h3 className="font-medium text-gray-800">{section.title}</h3>
                </div>
              )}
              
              <div className="flex items-center space-x-2">
                <button 
                  onClick={() => moveSectionUp(sectionIndex)}
                  disabled={sectionIndex === 0}
                  className={`p-1 rounded hover:bg-gray-200 ${sectionIndex === 0 ? 'text-gray-400 cursor-not-allowed' : 'text-gray-600'}`}
                  aria-label="Move section up"
                >
                  <ChevronUp size={16} />
                </button>
                <button 
                  onClick={() => moveSectionDown(sectionIndex)}
                  disabled={sectionIndex === sections.length - 1}
                  className={`p-1 rounded hover:bg-gray-200 ${sectionIndex === sections.length - 1 ? 'text-gray-400 cursor-not-allowed' : 'text-gray-600'}`}
                  aria-label="Move section down"
                >
                  <ChevronDown size={16} />
                </button>
                <button 
                  onClick={() => startEditSection(section)}
                  className="p-1 rounded hover:bg-gray-200 text-gray-600"
                  aria-label="Edit section"
                >
                  <Edit2 size={16} />
                </button>
                <button 
                  onClick={() => deleteSection(section.id)}
                  className="p-1 rounded hover:bg-gray-200 text-red-500"
                  aria-label="Delete section"
                >
                  <Trash2 size={16} />
                </button>
              </div>
            </div>
            
            {section.expanded && (
              <div className="section-content p-4">
                <div className="subsections space-y-2">
                  {section.subsections.map((subsection, subsectionIndex) => (
                    <div key={subsection.id} className="subsection-item flex items-center justify-between p-2 hover:bg-gray-50 rounded-md">
                      {editingSubsection === subsection.id ? (
                        <div className="flex items-center flex-grow mr-4">
                          <div className="w-6"></div> {/* Spacer to align with other items */}
                          <input 
                            type="text" 
                            value={newTitle} 
                            onChange={(e) => setNewTitle(e.target.value)}
                            className="flex-grow px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                            onKeyPress={(e) => e.key === 'Enter' && saveEditSubsection(section.id)}
                            autoFocus 
                          />
                          <button 
                            onClick={() => saveEditSubsection(section.id)}
                            className="ml-2 bg-green-500 hover:bg-green-600 text-white px-3 py-2 rounded-md"
                          >
                            Save
                          </button>
                        </div>
                      ) : (
                        <div className="flex items-center flex-grow">
                          <div className="w-6 text-gray-400">•</div>
                          <span className="text-gray-700">{subsection.title}</span>
                        </div>
                      )}
                      
                      <div className="flex items-center space-x-2">
                        <button 
                          onClick={() => moveSubsectionUp(section.id, subsectionIndex)}
                          disabled={subsectionIndex === 0}
                          className={`p-1 rounded hover:bg-gray-200 ${subsectionIndex === 0 ? 'text-gray-400 cursor-not-allowed' : 'text-gray-600'}`}
                          aria-label="Move subsection up"
                        >
                          <ChevronUp size={14} />
                        </button>
                        <button 
                          onClick={() => moveSubsectionDown(section.id, subsectionIndex, section.subsections)}
                          disabled={subsectionIndex === section.subsections.length - 1}
                          className={`p-1 rounded hover:bg-gray-200 ${subsectionIndex === section.subsections.length - 1 ? 'text-gray-400 cursor-not-allowed' : 'text-gray-600'}`}
                          aria-label="Move subsection down"
                        >
                          <ChevronDown size={14} />
                        </button>
                        <button 
                          onClick={() => startEditSubsection(subsection)}
                          className="p-1 rounded hover:bg-gray-200 text-gray-600"
                          aria-label="Edit subsection"
                        >
                          <Edit2 size={14} />
                        </button>
                        <button 
                          onClick={() => deleteSubsection(section.id, subsection.id)}
                          className="p-1 rounded hover:bg-gray-200 text-red-500"
                          aria-label="Delete subsection"
                        >
                          <Trash2 size={14} />
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
                
                <button 
                  onClick={() => addSubsection(section.id)}
                  className="mt-3 flex items-center space-x-1 text-blue-600 hover:text-blue-800 px-3 py-2 text-sm rounded-md hover:bg-blue-50 transition-colors"
                >
                  <PlusCircle size={16} />
                  <span>Add Subsection</span>
                </button>
              </div>
            )}
          </div>
        ))}
      </div>
      
      {sections.length === 0 && (
        <div className="empty-state text-center py-8">
          <p className="text-gray-500">No sections added yet. Click "Add Section" to start building your course structure.</p>
        </div>
      )}
    </div>
  );
};

export default StructureBuilder;
