import React from "react";
import { ChevronDown, ChevronRight, Plus, Edit2, Trash2, GripVertical } from "lucide-react";
import { Droppable, Draggable } from '@hello-pangea/dnd';

const Module = ({ 
  module, 
  moduleIndex, 
  toggleModule, 
  editModule, 
  deleteModule,
  addLesson,
  editLesson,
  deleteLesson
}) => {
  return (
    <Draggable
      key={module.id}
      draggableId={module.id}
      index={moduleIndex}
    >
      {(provided) => (
        <div 
          className="module-item"
          ref={provided.innerRef}
          {...provided.draggableProps}
        >
          <div className="module-header">
            <div 
              className="drag-handle"
              {...provided.dragHandleProps}
            >
              <GripVertical size={18} />
            </div>
            
            <button 
              onClick={() => toggleModule(module.id)}
              className="toggle-button"
            >
              {module.isOpen ? (
                <ChevronDown size={18} />
              ) : (
                <ChevronRight size={18} />
              )}
            </button>
            
            <div className="module-info">
              <input
                type="text"
                value={module.title}
                onChange={(e) => editModule(module.id, 'title', e.target.value)}
                className="module-title-input"
              />
              <input
                type="text"
                value={module.description}
                onChange={(e) => editModule(module.id, 'description', e.target.value)}
                className="module-description-input"
              />
            </div>
            
            <div className="module-actions">
              <button 
                onClick={() => deleteModule(module.id)}
                className="btn-icon-delete"
                aria-label="Delete module"
              >
                <Trash2 size={16} />
              </button>
            </div>
          </div>
          
          {module.isOpen && (
            <div className="lessons-container">
              <Droppable
                droppableId={module.id}
                type="LESSON"
              >
                {(provided) => (
                  <div
                    ref={provided.innerRef}
                    {...provided.droppableProps}
                    className="lessons-list"
                  >
                    {module.lessons.map((lesson, lessonIndex) => (
                      <Draggable
                        key={lesson.id}
                        draggableId={lesson.id}
                        index={lessonIndex}
                      >
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            className="lesson-item"
                          >
                            <div 
                              className="lesson-drag-handle"
                              {...provided.dragHandleProps}
                            >
                              <GripVertical size={16} />
                            </div>
                            <div className="lesson-info">
                              <div className="lesson-title">{lesson.title}</div>
                              <div className="lesson-duration">{lesson.duration}</div>
                            </div>
                            <div className="lesson-actions">
                              <button
                                onClick={() => editLesson(lesson, module.id)}
                                className="btn-icon-edit"
                                aria-label="Edit lesson"
                              >
                                <Edit2 size={14} />
                              </button>
                              <button
                                onClick={() => deleteLesson(lesson.id, module.id)}
                                className="btn-icon-delete"
                                aria-label="Delete lesson"
                              >
                                <Trash2 size={14} />
                              </button>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                    
                    <button
                      onClick={() => addLesson(module.id)}
                      className="btn-add-lesson"
                    >
                      <Plus size={16} className="icon-plus" />
                      Add Lesson
                    </button>
                  </div>
                )}
              </Droppable>
            </div>
          )}
        </div>
      )}
    </Draggable>
  );
};

export default Module;
