import React from 'react';
import { Search, BookOpen, Award, PlayCircle } from 'lucide-react';

const HowItWorks = () => {
  const steps = [
    {
      id: 1,
      icon: <Search className="h-10 w-10" />,
      title: "Find Your Course",
      description: "Browse our extensive library of courses in various categories and skill levels."
    },
    {
      id: 2,
      icon: <PlayCircle className="h-10 w-10" />,
      title: "Learn at Your Pace",
      description: "Access high-quality video lectures, quizzes, and assignments anytime, anywhere."
    },
    {
      id: 3,
      icon: <BookOpen className="h-10 w-10" />,
      title: "Practice with Projects",
      description: "Apply your knowledge with hands-on projects and get feedback from instructors."
    },
    {
      id: 4,
      icon: <Award className="h-10 w-10" />,
      title: "Earn Certificates",
      description: "Complete courses to earn certificates that showcase your skills to employers."
    }
  ];

  return (
    <section className="py-16 bg-white">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="text-center mb-14">
          <h2 className="text-3xl font-bold text-gray-900">How It Works</h2>
          <p className="mt-4 text-lg text-gray-600 max-w-3xl mx-auto">
            Our platform is designed to make learning accessible, engaging, and effective. Follow these simple steps to begin your learning journey.
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {steps.map((step) => (
            <div key={step.id} className="relative">
              <div className="bg-teal-50 rounded-lg p-8 h-full">
                <div className="absolute top-0 right-0 bg-teal-600 text-white w-8 h-8 flex items-center justify-center rounded-full -mt-3 -mr-3">
                  {step.id}
                </div>
                <div className="text-teal-600 mb-4">{step.icon}</div>
                <h3 className="text-xl font-bold text-gray-900 mb-2">{step.title}</h3>
                <p className="text-gray-600">{step.description}</p>
              </div>
              
              {step.id !== steps.length && (
                <div className="hidden lg:block absolute top-1/2 left-full w-8 h-0.5 bg-gray-200 transform -translate-y-1/2">
                  <div className="absolute right-0 w-2 h-2 rounded-full bg-teal-600 transform -translate-y-1/2"></div>
                </div>
              )}
            </div>
          ))}
        </div>

        <div className="mt-12 text-center">
          <button className="bg-teal-600 text-white px-6 py-3 rounded-md font-medium hover:bg-teal-700 shadow-md">
            Start Learning Now
          </button>
        </div>
      </div>
    </section>
  );
};

export default HowItWorks;
