import React, { useState, useEffect } from "react";
import CourseCard from "../components/CourseCard";
import Navbar from '../components/Home/Navbar';
import Footer from '../components/Home/Footer';
import '../css/BrowseCourses.css';
import { Search, Filter, Bookmark, Clock, User } from "lucide-react";

const mockCourses = [
  {
    id: 1,
    title: "Intro to Python",
    instructor: "Jane Doe",
    duration: "4 weeks",
    level: "Beginner",
    rating: 4.7,
    studentsEnrolled: 1254,
    price: 49.99,
    image: "/api/placeholder/400/250",
    topics: ["Python", "Programming", "Beginner"],
  },
  {
    id: 2,
    title: "Web Development Bootcamp",
    instructor: "John Smith",
    duration: "6 weeks",
    level: "Intermediate",
    rating: 4.9,
    studentsEnrolled: 3421,
    price: 79.99,
    image: "/api/placeholder/400/250",
    topics: ["HTML", "CSS", "JavaScript"],
  },
  {
    id: 3,
    title: "Data Science Basics",
    instructor: "Alice Brown",
    duration: "5 weeks",
    level: "Beginner",
    rating: 4.5,
    studentsEnrolled: 987,
    price: 59.99,
    image: "/api/placeholder/400/250",
    topics: ["Data Analysis", "Pandas", "Beginner"],
  },
  {
    id: 4,
    title: "Advanced React Patterns",
    instructor: "Mike Johnson",
    duration: "4 weeks",
    level: "Advanced",
    rating: 4.8,
    studentsEnrolled: 765,
    price: 89.99,
    image: "/api/placeholder/400/250",
    topics: ["React", "JavaScript", "Advanced"],
  },
  {
    id: 5,
    title: "Machine Learning Fundamentals",
    instructor: "Sarah Williams",
    duration: "8 weeks",
    level: "Intermediate",
    rating: 4.6,
    studentsEnrolled: 1876,
    price: 99.99,
    image: "/api/placeholder/400/250",
    topics: ["Machine Learning", "Python", "Data Science"],
  },
  {
    id: 6,
    title: "UX/UI Design Principles",
    instructor: "David Lee",
    duration: "5 weeks",
    level: "Beginner",
    rating: 4.7,
    studentsEnrolled: 1134,
    price: 69.99,
    image: "/api/placeholder/400/250",
    topics: ["Design", "UX", "UI"],
  }
];

const BrowseCourses = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [courses, setCourses] = useState([]);
  const [filteredCourses, setFilteredCourses] = useState([]);
  const [filters, setFilters] = useState({
    level: "",
    priceRange: "",
    topics: [],
    duration: ""
  });
  const [showFilters, setShowFilters] = useState(false);
  const [sortBy, setSortBy] = useState("popular");

  useEffect(() => {
    // Simulating API fetch with a small delay
    setTimeout(() => {
      setCourses(mockCourses);
      setFilteredCourses(mockCourses);
    }, 500);
  }, []);

  useEffect(() => {
    // Apply filters and search
    let result = [...courses];
    
    // Apply search query
    if (searchQuery) {
      result = result.filter(
        course => 
          course.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
          course.instructor.toLowerCase().includes(searchQuery.toLowerCase()) ||
          course.topics.some(topic => topic.toLowerCase().includes(searchQuery.toLowerCase()))
      );
    }
    
    // Apply level filter
    if (filters.level) {
      result = result.filter(course => course.level === filters.level);
    }
    
    // Apply price range filter
    if (filters.priceRange) {
      switch (filters.priceRange) {
        case "free":
          result = result.filter(course => course.price === 0);
          break;
        case "under50":
          result = result.filter(course => course.price > 0 && course.price < 50);
          break;
        case "50to100":
          result = result.filter(course => course.price >= 50 && course.price <= 100);
          break;
        case "over100":
          result = result.filter(course => course.price > 100);
          break;
        default:
          break;
      }
    }
    
    // Apply topics filter
    if (filters.topics.length > 0) {
      result = result.filter(course => 
        filters.topics.some(topic => course.topics.includes(topic))
      );
    }
    
    // Apply duration filter
    if (filters.duration) {
      switch (filters.duration) {
        case "short":
          result = result.filter(course => parseInt(course.duration) <= 4);
          break;
        case "medium":
          result = result.filter(course => parseInt(course.duration) > 4 && parseInt(course.duration) <= 6);
          break;
        case "long":
          result = result.filter(course => parseInt(course.duration) > 6);
          break;
        default:
          break;
      }
    }
    
    // Apply sorting
    if (sortBy === "popular") {
      result.sort((a, b) => b.studentsEnrolled - a.studentsEnrolled);
    } else if (sortBy === "rating") {
      result.sort((a, b) => b.rating - a.rating);
    } else if (sortBy === "priceLow") {
      result.sort((a, b) => a.price - b.price);
    } else if (sortBy === "priceHigh") {
      result.sort((a, b) => b.price - a.price);
    }
    
    setFilteredCourses(result);
  }, [searchQuery, courses, filters, sortBy]);

  const handleFilterChange = (filterKey, value) => {
    setFilters({
      ...filters,
      [filterKey]: value
    });
  };

  const handleTopicToggle = (topic) => {
    const updatedTopics = filters.topics.includes(topic)
      ? filters.topics.filter(t => t !== topic)
      : [...filters.topics, topic];
      
    setFilters({
      ...filters,
      topics: updatedTopics
    });
  };

  const resetFilters = () => {
    setFilters({
      level: "",
      priceRange: "",
      topics: [],
      duration: ""
    });
    setSearchQuery("");
  };

  // Get all unique topics from courses
  const allTopics = Array.from(
    new Set(courses.flatMap(course => course.topics))
  );

  return (
    <div className="min-h-screen flex flex-col bg-gray-50">
      <Navbar />
      <div className="container mx-auto px-4 py-8 flex-grow">
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-800 mb-2">Browse Courses</h1>
          <p className="text-gray-600">Discover courses to enhance your skills and advance your career</p>
        </div>

        <div className="flex flex-col md:flex-row gap-6">
          {/* Filters Panel (sidebar) */}
          <div className={`w-full md:w-64 md:block ${showFilters ? 'block' : 'hidden'}`}>
            <div className="bg-white rounded-lg shadow p-4 sticky top-4">
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-lg font-semibold text-gray-800">Filters</h2>
                <button 
                  onClick={resetFilters}
                  className="text-blue-600 text-sm hover:underline"
                >
                  Reset All
                </button>
              </div>
              
              {/* Level Filter */}
              <div className="mb-4">
                <h3 className="font-medium text-gray-700 mb-2">Level</h3>
                <div className="space-y-2">
                  {["Beginner", "Intermediate", "Advanced"].map(level => (
                    <label key={level} className="flex items-center cursor-pointer">
                      <input
                        type="radio"
                        name="level"
                        checked={filters.level === level}
                        onChange={() => handleFilterChange("level", level)}
                        className="mr-2"
                      />
                      <span className="text-gray-600">{level}</span>
                    </label>
                  ))}
                </div>
              </div>
              
              {/* Price Range Filter */}
              <div className="mb-4">
                <h3 className="font-medium text-gray-700 mb-2">Price</h3>
                <div className="space-y-2">
                  {[
                    { value: "free", label: "Free" },
                    { value: "under50", label: "Under $50" },
                    { value: "50to100", label: "$50 - $100" },
                    { value: "over100", label: "Over $100" }
                  ].map(option => (
                    <label key={option.value} className="flex items-center cursor-pointer">
                      <input
                        type="radio"
                        name="priceRange"
                        checked={filters.priceRange === option.value}
                        onChange={() => handleFilterChange("priceRange", option.value)}
                        className="mr-2"
                      />
                      <span className="text-gray-600">{option.label}</span>
                    </label>
                  ))}
                </div>
              </div>
              
              {/* Duration Filter */}
              <div className="mb-4">
                <h3 className="font-medium text-gray-700 mb-2">Duration</h3>
                <div className="space-y-2">
                  {[
                    { value: "short", label: "Short (≤ 4 weeks)" },
                    { value: "medium", label: "Medium (5-6 weeks)" },
                    { value: "long", label: "Long (> 6 weeks)" }
                  ].map(option => (
                    <label key={option.value} className="flex items-center cursor-pointer">
                      <input
                        type="radio"
                        name="duration"
                        checked={filters.duration === option.value}
                        onChange={() => handleFilterChange("duration", option.value)}
                        className="mr-2"
                      />
                      <span className="text-gray-600">{option.label}</span>
                    </label>
                  ))}
                </div>
              </div>
              
              {/* Topics Filter */}
              <div>
                <h3 className="font-medium text-gray-700 mb-2">Topics</h3>
                <div className="space-y-2 max-h-48 overflow-y-auto">
                  {allTopics.map(topic => (
                    <label key={topic} className="flex items-center cursor-pointer">
                      <input
                        type="checkbox"
                        checked={filters.topics.includes(topic)}
                        onChange={() => handleTopicToggle(topic)}
                        className="mr-2"
                      />
                      <span className="text-gray-600">{topic}</span>
                    </label>
                  ))}
                </div>
              </div>
            </div>
          </div>
          
          {/* Main Content */}
          <div className="flex-1">
            {/* Search and Sort Bar */}
            <div className="flex flex-col md:flex-row gap-4 mb-6">
              <div className="relative flex-1">
                <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                  <Search size={18} className="text-gray-400" />
                </div>
                <input
                  type="text"
                  placeholder="Search courses, instructors, or topics..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10 pr-4 py-2 w-full rounded-lg border border-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              
              <div className="flex gap-2">
                <button
                  onClick={() => setShowFilters(!showFilters)}
                  className="md:hidden flex items-center px-4 py-2 bg-white border border-gray-300 rounded-lg hover:bg-gray-50"
                >
                  <Filter size={18} className="mr-2" />
                  Filters
                </button>
                
                <select
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                  className="px-4 py-2 bg-white border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="popular">Most Popular</option>
                  <option value="rating">Highest Rated</option>
                  <option value="priceLow">Price: Low to High</option>
                  <option value="priceHigh">Price: High to Low</option>
                </select>
              </div>
            </div>
            
            {/* Results */}
            <div>
              <div className="text-gray-600 mb-4">
                {filteredCourses.length} {filteredCourses.length === 1 ? 'course' : 'courses'} found
              </div>
              
              {filteredCourses.length > 0 ? (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {filteredCourses.map((course) => (
                    <div key={course.id} className="bg-white rounded-lg overflow-hidden shadow hover:shadow-lg transition-shadow duration-300">
                      <img 
                        src={course.image} 
                        alt={course.title} 
                        className="w-full h-48 object-cover"
                      />
                      <div className="p-4">
                        <div className="flex justify-between items-start">
                          <h3 className="text-lg font-semibold text-gray-800 mb-2">{course.title}</h3>
                          <button className="text-gray-400 hover:text-blue-500">
                            <Bookmark size={20} />
                          </button>
                        </div>
                        
                        <div className="flex items-center mb-2">
                          <User size={16} className="text-gray-500 mr-1" />
                          <span className="text-gray-600 text-sm">{course.instructor}</span>
                        </div>
                        
                        <div className="flex items-center mb-2">
                          <Clock size={16} className="text-gray-500 mr-1" />
                          <span className="text-gray-600 text-sm">{course.duration}</span>
                        </div>
                        
                        <div className="flex items-center mb-3">
                          <span className="text-yellow-500 mr-1">★</span>
                          <span className="text-gray-700">{course.rating}</span>
                          <span className="text-gray-500 text-sm ml-2">({course.studentsEnrolled} students)</span>
                        </div>
                        
                        <div className="flex flex-wrap gap-1 mb-4">
                          {course.topics.map(topic => (
                            <span key={topic} className="px-2 py-1 bg-blue-50 text-blue-700 text-xs rounded">
                              {topic}
                            </span>
                          ))}
                        </div>
                        
                        <div className="flex justify-between items-center">
                          <span className="text-lg font-bold text-gray-800">${course.price.toFixed(2)}</span>
                          <button className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition-colors">
                            Enroll Now
                          </button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="py-12 text-center">
                  <p className="text-lg text-gray-600 mb-4">No courses match your search criteria.</p>
                  <button 
                    onClick={resetFilters}
                    className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                  >
                    Clear Filters
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default BrowseCourses;
