import React, { useState } from "react";
import CourseBasicInfoForm from "../../components/CourseBasicInfoForm";
import CourseStructureBuilder from "../../components/CourseStructureBuilder";
import CourseResourcesUploader from "../../components/CourseResourcesUploader";
import CourseSettingsForm from "../../components/CourseSettingsForm";
import CourseFinalReview from "../../components/CourseFinalReview";

import "../styles/CreateCourse.css"; // optional external styles

const TABS = [
  "Basic Info",
  "Structure",
  "Resources",
  "Settings",
  "Preview & Publish"
];

const CreateCourse = () => {
  const [activeTab, setActiveTab] = useState(0);

  const renderTabContent = () => {
    switch (activeTab) {
      case 0:
        return <CourseBasicInfoForm />;
      case 1:
        return <CourseStructureBuilder />;
      case 2:
        return <CourseResourcesUploader />;
      case 3:
        return <CourseSettingsForm />;
      case 4:
        return <CourseFinalReview />;
      default:
        return null;
    }
  };

  return (
    <div className="create-course-container">
      <h2 className="page-title">Create a New Course</h2>
      <div className="tabs">
        {TABS.map((tab, index) => (
          <button
            key={index}
            className={`tab-button ${activeTab === index ? "active" : ""}`}
            onClick={() => setActiveTab(index)}
          >
            {tab}
          </button>
        ))}
      </div>

      <div className="tab-content">{renderTabContent()}</div>
    </div>
  );
};

export default CreateCourse;
