import React, { useState } from 'react';
import { Calendar, Video, FileText, CheckSquare, ChevronRight } from 'lucide-react';
import '../../../css/teacher/UpcomingItems.css';

const UpcomingItems = () => {
  const today = new Date();
  const dateOptions = { weekday: 'short', month: 'short', day: 'numeric' };
  const formattedDate = today.toLocaleDateString('en-US', dateOptions);
  
  const [viewAll, setViewAll] = useState(false);
  
  const upcomingEvents = [
    {
      id: 1,
      type: 'class',
      title: 'Live Session: Advanced Algorithms',
      course: 'Data Structures for Beginners',
      date: 'Today',
      time: '2:00 PM - 3:30 PM',
      status: 'upcoming'
    },
    {
      id: 2,
      type: 'assignment',
      title: 'Final Project Review',
      course: 'Advanced React Patterns',
      date: 'Tomorrow',
      time: 'Due by 11:59 PM',
      status: 'pending'
    },
    {
      id: 3,
      type: 'recording',
      title: 'Record React Hooks Lecture',
      course: 'Advanced React Patterns',
      date: 'Apr 26',
      time: 'No scheduled time',
      status: 'todo'
    },
    {
      id: 4,
      type: 'grading',
      title: 'Grade Quiz 3 Submissions',
      course: 'Introduction to Web Development',
      date: 'Apr 27',
      time: '15 submissions pending',
      status: 'pending'
    },
    {
      id: 5,
      type: 'class',
      title: 'Office Hours',
      course: 'Data Structures for Beginners',
      date: 'Apr 28',
      time: '10:00 AM - 12:00 PM',
      status: 'upcoming'
    },
    {
      id: 6,
      type: 'assignment',
      title: 'Create Final Exam',
      course: 'Introduction to Web Development',
      date: 'Apr 30',
      time: 'No due date',
      status: 'todo'
    }
  ];
  
  const getIcon = (type) => {
    switch(type) {
      case 'class':
        return <Video size={18} />;
      case 'assignment':
        return <FileText size={18} />;
      case 'recording':
        return <Video size={18} />;
      case 'grading':
        return <CheckSquare size={18} />;
      default:
        return <Calendar size={18} />;
    }
  };
  
  const getTypeLabel = (type) => {
    switch(type) {
      case 'class':
        return 'Live Class';
      case 'assignment':
        return 'Assignment';
      case 'recording':
        return 'Recording';
      case 'grading':
        return 'Grading';
      default:
        return 'Event';
    }
  };
  
  const getStatusClass = (status) => {
    switch(status) {
      case 'upcoming':
        return 'status-upcoming';
      case 'pending':
        return 'status-pending';
      case 'todo':
        return 'status-todo';
      default:
        return '';
    }
  };

  const displayItems = viewAll ? upcomingEvents : upcomingEvents.slice(0, 4);

  return (
    <div className="upcoming-container">
      <div className="upcoming-header">
        <div className="date-display">
          <Calendar size={16} />
          <span>{formattedDate}</span>
        </div>
        <div className="filter-options">
          <select defaultValue="all">
            <option value="all">All Tasks</option>
            <option value="classes">Live Classes</option>
            <option value="assignments">Assignments</option>
            <option value="recordings">Recordings</option>
            <option value="grading">Grading</option>
          </select>
        </div>
      </div>
      
      <div className="upcoming-list">
        {displayItems.map(event => (
          <div key={event.id} className="upcoming-item">
            <div className="item-icon">
              {getIcon(event.type)}
            </div>
            <div className="item-content">
              <div className="item-title-row">
                <span className="item-type">{getTypeLabel(event.type)}</span>
                <span className={`item-status ${getStatusClass(event.status)}`}>
                  {event.status === 'upcoming' ? 'Upcoming' : 
                   event.status === 'pending' ? 'Action Required' : 'To Do'}
                </span>
              </div>
              <h4 className="item-title">{event.title}</h4>
              <div className="item-details">
                <span className="item-course">{event.course}</span>
                <div className="item-time">
                  <span className="item-date">{event.date}</span>
                  <span className="item-hour">{event.time}</span>
                </div>
              </div>
            </div>
            <button className="item-action">
              <ChevronRight size={16} />
            </button>
          </div>
        ))}
      </div>
      
      {upcomingEvents.length > 4 && (
        <button 
          className="view-all-button"
          onClick={() => setViewAll(!viewAll)}
        >
          {viewAll ? 'Show Less' : `View All (${upcomingEvents.length})`}
        </button>
      )}
    </div>
  );
};

export default UpcomingItems;
